import React, { useState } from 'react'

// Import Components
import Product from "./Product"

// Import SVG Assets
import { ReactComponent as UpArrow } from "../../assets/up-arrow.svg"
import { ReactComponent as DownArrow } from "../../assets/down-arrow.svg"

const ProductsContainer = () => {

    // Component State
    const [products, setProducts] = useState([
        {
            name: "Wealth",
            value: 500000,
            growth: true,
            percentage: 25.3,
        },
        {
            name: "Cash",
            value: 260000,
            growth: true,
            percentage: 15.3,
        },
        {
            name: "Venture",
            value: 928000,
            growth: false,
            percentage: 0.13,
        },
        {
            name: "Private Equity",
            value: 850000,
            growth: true,
            percentage: 50.8,
        },
        {
            name: "Real State",
            value: 799000,
            growth: true,
            percentage: 25.6,
        },
    ])

    // Component Functions
    const sortHigh = () => {
        const sorted = products.sort((a, b) => {
        return a.value - b.value;
        });
        console.log(sorted);
        setProducts([...sorted]);
    };

    const sortLow = () => {
        const sorted = products.sort((a, b) => {
        return b.value - a.value;
        });
        console.log(sorted);
        setProducts([...sorted]);
    };

    return (
        <div>
            <div>
                <p>Sort:</p>
                <button onClick={sortHigh}>
                    highest values first <UpArrow />
                </button>
                <button onClick={sortLow}>
                    highest values last <DownArrow />
                </button>
            </div>
            <div>
                {products.map((product, index) => {
                    return (
                     <Product key={index} name={product.name} value={product.value.toLocaleString()} growth={product.growth} percentage={product.percentage} />   
                    )
                })}
            </div>
        </div>
    )
}

export default ProductsContainer
