import React, { useState } from 'react'

// Import SVG Assets
import { ReactComponent as Logo } from "../../assets/logo.svg"
import { ReactComponent as Logout } from "../../assets/exit.svg"
import { ReactComponent as Home } from "../../assets/home.svg"
import { ReactComponent as Profile } from "../../assets/user.svg"
import { ReactComponent as Settings } from "../../assets/settings.svg"
import { ReactComponent as Hamburger } from "../../assets/hamburger.svg"
import { ReactComponent as CloseMenu } from "../../assets/closemenu.svg"
import { ReactComponent as Liqid } from "../../assets/liqid.svg"

// React responsive navbar: https://sdever.medium.com/building-a-responsive-navigation-bar-with-react-css-d9b30af03c20

const Nav = (props) => {

    // Component State
    const [dropdown, setDropdown] = useState(false)
    const [click, setClick] = useState(false);
    const handleClick = () => {
        setClick(!click)
        setDropdown(!dropdown)
    };
    const closeMobileMenu = () => {
        setClick(false)
        setDropdown(false)
    };

    return (
        <>
            {dropdown ? (
            <div className="mobilenav mobilenav-red">
                <div className="mobilenav__hello">
                    <div className="logo mobilenav-red__logo">
                        <Logo className="logo__svg mobilenav-red__logo__svg" />
                    </div>
                    <em><p className="mobilenav-red__user">Hallo, <span>{props.user}</span>!</p></em>
                </div>
                <div className="mobile-menu" onClick={handleClick}>
                    {click ? (
                    <CloseMenu className="menu-icon" />
                    ) : (
                    <Hamburger className="menu-icon" />
                    )}
                </div>
            </div>)
            :
            (<div className="mobilenav">
                <div className="mobilenav__hello">
                    <div className="logo">
                        <Logo className="logo__svg" />
                    </div>
                    <em><p className="mobilenav__hello__user">Hallo, <span>{props.user}</span>!</p></em>
                </div>
                <div className="mobile-menu" onClick={handleClick}>
                    {click ? (
                    <CloseMenu className="mobile-menu__close" />
                    ) : (
                    <Hamburger className="mobile-menu__hamburger" />
                    )}
                </div>
            </div>)}
            <div className={click ? "nav-options active" : "nav-options"}>
                <a className="nav-item dropdown-item" href="/logout" onClick={closeMobileMenu}><Logout className="nav-item__icon" />Logout</a>
                <a className="nav-item dropdown-item" href="/" onClick={closeMobileMenu}><Home className="nav-item__icon" />Home</a>
                <a className="nav-item dropdown-item" href="/profile" onClick={closeMobileMenu}><Profile className="nav-item__icon" />Profile</a>
                <a className="nav-item dropdown-item" href="/settings" onClick={closeMobileMenu}><Settings className="nav-item__icon" />Settings</a>
                <Liqid className="nav-options__liqid" />
                <img src="liqid.svg" />
            </div>
        </>
    )
}

export default Nav
