import React, { useState } from 'react'

// Import SVG Assets
import { ReactComponent as Logo } from "../../assets/logo.svg"
import { ReactComponent as Logout } from "../../assets/exit.svg"
import { ReactComponent as Home } from "../../assets/home.svg"
import { ReactComponent as Profile } from "../../assets/user.svg"
import { ReactComponent as Settings } from "../../assets/settings.svg"
import { ReactComponent as Hamburger } from "../../assets/hamburger.svg"
import { ReactComponent as CloseMenu } from "../../assets/closemenu.svg"

// React responsive navbar: https://sdever.medium.com/building-a-responsive-navigation-bar-with-react-css-d9b30af03c20

const Nav = (props) => {

    // Component State
    const [dropdown, setDropdown] = useState(false)
    const [click, setClick] = useState(false);
    const handleClick = () => {
        setClick(!click)
        setDropdown(!dropdown)
    };
    const closeMobileMenu = () => {
        setClick(false)
        setDropdown(false)
    };

    return (
        <>
            {dropdown ? (
            <div className="mobilenav-red">
                <div className="mobilenav-red__hello">
                    <div className="mobilenav-red__hello__logo">
                        <Logo className="mobilenav-red__hello__logo__svg" />
                    </div>
                    <p className="mobilenav-red__hello__user">Hallo, <span>{props.user}</span>!</p>
                </div>
                <div className="mobile-menu" onClick={handleClick}>
                    {click ? (
                    <CloseMenu className="menu-icon" />
                    ) : (
                    <Hamburger className="menu-icon" />
                    )}
                </div>
            </div>)
            :
            (<div className="mobilenav">
                <div className="mobilenav__hello">
                    <div className="mobilenav__hello__logo">
                        <Logo className="mobilenav__hello__logo__svg" />
                    </div>
                    <p className="mobilenav__hello__user">Hallo, <span>{props.user}</span>!</p>
                </div>
                <div className="mobile-menu" onClick={handleClick}>
                    {click ? (
                    <CloseMenu className="menu-icon" />
                    ) : (
                    <Hamburger className="menu-icon" />
                    )}
                </div>
            </div>)}
            <div className={click ? "nav-options active" : "nav-options"}>
                    <a className="nav-options__item" href="/logout"><Logout />Logout</a>
                <li className="nav-options__item" onClick={closeMobileMenu}>
                    <a href="/home"><Home />Home</a>
                </li>
                <li className="nav-options__item" onClick={closeMobileMenu}>
                    <a href="/profile"><Profile />Profile</a>
                </li>
                <li className="nav-options__item" onClick={closeMobileMenu}>
                    <a href="/settings"><Settings />Settings</a>
                </li>
            </div>
        </>
    )
}

export default Nav
