import React, { useState } from 'react'

// Import SVG Assets
import { ReactComponent as Logo } from "../../assets/logo.svg"
import { ReactComponent as Logout } from "../../assets/exit.svg"
import { ReactComponent as Home } from "../../assets/home.svg"
import { ReactComponent as Profile } from "../../assets/user.svg"
import { ReactComponent as Settings } from "../../assets/settings.svg"
import { ReactComponent as Hamburger } from "../../assets/hamburger.svg"
import { ReactComponent as CloseMenu } from "../../assets/closemenu.svg"

// React responsive navbar: https://sdever.medium.com/building-a-responsive-navigation-bar-with-react-css-d9b30af03c20

const Nav = (props) => {

    const [click, setClick] = useState(false);
    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    return (
        // <div className="nav">
        //     <div>
        //         <div>
        //             <Logo />
        //         </div>
        //         <p>Hallo, <span>{ props.user }</span>!</p>
        //         <div>
        //             <Logout />
        //             <p>Logout</p>
        //         </div>
        //     </div>
        //     <div>
        //         <div>
        //             <Home />
        //             <a href="/home">Home</a>
        //         </div>
        //         <div>
        //             <Profile />
        //             <a href="/profile">Profile</a>
        //         </div>
        //         <div>
        //             <Settings />
        //             <a href="/settings">Settings</a>
        //         </div>
        //     </div>
        // </div>

        <div className="nav">
      <div>
        <div>
          <a href="#">
            <Logo className="logo" />
          </a>
          <p>Hallo, <span>{ props.user }</span>!</p>
            <div>
                <Logout />
                <p>Logout</p>
            </div>
        </div>

        <ul className={click ? "nav-options active" : "nav-options"}>
          <li className="option" onClick={closeMobileMenu}>
             <Home />
             <a href="/home">Home</a>
          </li>
          <li className="option" onClick={closeMobileMenu}>
            <Profile />
            <a href="/profile">Profile</a>
          </li>
          <li className="option" onClick={closeMobileMenu}>
            <Settings />
            <a href="/settings">Settings</a>
          </li>
          <li className="option mobile-option" onClick={closeMobileMenu}>
            <a href="#">SIGN-IN</a>
          </li>
          <li className=" option mobile-option" onClick={closeMobileMenu}>
            <a href="" className="sign-up">
              SIGN-UP
            </a>
          </li>
        </ul>
      </div>
      <ul className="signin-up">
        <li className="sign-in" onClick={closeMobileMenu}>
          <a href="#">SIGN-IN</a>
        </li>
        <li onClick={closeMobileMenu}>
          <a href="" className="signup-btn">
            SIGN-UP
          </a>
        </li>
      </ul>
      <div className="mobile-menu" onClick={handleClick}>
        {click ? (
          <CloseMenu className="menu-icon" />
        ) : (
          <Hamburger className="menu-icon" />
        )}
      </div>
    </div>
    )
}

export default Nav
