import React, { useState } from 'react'

// Import Components
import Product from "./Product"

// Import SVG Assets
import { ReactComponent as UpArrow } from "../../assets/up-arrow.svg"
import { ReactComponent as DownArrow } from "../../assets/down-arrow.svg"

const ProductsContainer = () => {

    // Component State
    const [products, setProducts] = useState([
        {
            name: "Wealth",
            value: 500000,
            growth: true,
            percentage: 25.3,
        },
        {
            name: "Cash",
            value: 260000,
            growth: true,
            percentage: 15.3,
        },
        {
            name: "Venture",
            value: 928000,
            growth: false,
            percentage: 0.13,
        },
        {
            name: "Private Equity",
            value: 850000,
            growth: true,
            percentage: 50.8,
        },
        {
            name: "Real State",
            value: 799000,
            growth: true,
            percentage: 25.6,
        },
    ])

    // Component Functions
    // Sort products by highest value
    const sortHigh = () => {
        const sorted = products.sort((a, b) => {
        return b.value - a.value;
        });
        setProducts([...sorted]);
    };

    // Sort products by lowest value
    const sortLow = () => {
        const sorted = products.sort((a, b) => {
        return a.value - b.value;
        });
        setProducts([...sorted]);
    };

    return (
        <div className="products-container">
            <div className="product-sort">
                <p className="product-sort__name">Sort:</p>
                <input className="product-sort__radio" type="radio" name="sort" id="high" onChange={sortHigh}></input>
                <label className="product-sort__button" htmlFor="high">highest values first <UpArrow className="product-sort__button__icon" /></label>
                <input className="product-sort__radio" type="radio" name="sort" id="low" onChange={sortLow}></input>
                <label className="product-sort__button" htmlFor="low">highest values last <DownArrow className="product-sort__button__icon" /></label>
            </div>
            {products.map((product, index) => {
                return (
                    <Product key={index} name={product.name} value={product.value.toLocaleString()} growth={product.growth} percentage={product.percentage} />   
                )
            })}
        </div>
    )
}

export default ProductsContainer
